import PopupWithForm from "./PopupWithForm.js";
import InputLink from "./InputLink.js";
import { useRef } from "react";

export default function EditAvatarPopup(props) {
	const inputLink = useRef("");

	function handleFormSubmit(e) {
		e.preventDefault();
		props.onUpdateAvatar({
			avatar: inputLink.current.value,
		});
		inputLink.current.value = "";
	}

	return (
		<PopupWithForm
			name="edit-avatar"
			title="Обновить аватар"
			button="Сохранить"
			popupContent="popup__container_content_edit-avatar"
			isOpen={props.isOpen}
			onClose={props.onClose}
			onSubmit={handleFormSubmit}
		>
			<InputLink placeholder="Ссылка на картинку" link={inputLink} id="owner-avatar" type="url" name="avatar" className="popup__input popup__input_type_img-link"
              value="" pattern="https?://.+" required  />
		</PopupWithForm>
	);
}
